INSERT INTO `ref_soal` (`soal_id`, `soal_isi`, `soal_pembahasan`, `soal_jawaban_pembahasan`, `soal_jawaban_index`) VALUES
(1, 'Istilah yang digunakan untuk menyebut sebuah aktivitas bercerita yang umumnya dilakukan oleh orang Bali disebut…', 'Mesatua Bali sama dengan mendongeng Bali, istilah yang digunakan untuk menyebut sebuah aktivitas bercerita yang umumnya dilakukan oleh orang tua sebelum tidur', 'a', 1),
(2, 'Secara etimologis kata satua menurut bahasa tuturan masyarakat Bali berarti…', 'Secara etimologis kata satua menurut bahasa tuturan masyarakat Bali, berarti berbicara atau bercerita.', 'd', 4),
(3, 'Satua Bali termasuk dalam jenis sastra…', 'Satua Bali umumnya berbentuk lisan', 'c', 3),
(4, 'Satua Bali umumnya berbentuk lisan, namun belakangan demi kelestariannya ditranskripsikan ke dalam teks-teks satua Bali yang banyak disimpan di…', 'Demi kelestariannya satua Bali ditranskripsikan ke dalam teks-teks satua Bali yang banyak disimpan di Gedong Kirtya Singaraja, Bali.', 'b', 2),
(5, 'Menurut Bagus (1979:13) sastra Bali tradisional kalau dilihat dari bentuk penyajiannya dapat dibagi menjadi…', 'Menurut Bagus (1979:13) sastra Bali tradisional kalau dilihat dari bentuk penyajiannya dapat dibagi atas; sastra lisan dan sastra tulisan.', 'd', 4),
(6, 'Dalam mempelajari satua Bali terdapat beberapa teknik yang harus dipelajari terdiri dari…', 'Teknik dasar masatua Bali terdiri dari; bahasa atau vokal, gerak tubuh, ekspresi atau penghayatan, penguasaan materi, amanat atau pesan', 'b', 2),
(7, 'Struktur bahasa yang digunakan dalam satua Bali pada umumnya menggunakan bahasa…', 'Struktur bahasa yang digunakan dalam satua Bali pada umumnya menggunakan bahasa kepara atau bahasa yang digunakan dalam kehidupan sehari-hari ketika mengadakan interaksi dengan lawan bicaranya.', 'b', 2),
(8, 'Gaya bercerita berkaitan dengan karakter tokoh yang diperankan disebut…', 'Gerak tubuh juga mendukung penampilan dari pendongeng atau pembawa cerita. Meskipun gerak tubuh merupakan hak pendongeng atau pembawa cerita, namun gerak tubuh tetap berkaitan dengan ekspresi. Oleh karena itu gaya bercerita berkaitan dengan karakter tokoh yang diperankan. ', 'b', 2),
(9, 'Penguasaan karakter tokoh cerita pada jiwa pendongeng yang dapat mewakili tokoh sehingga dapat menyangkut bagaimana karakter tokoh yang keras, lembut, lucu, dan lugu disebut…', 'Ekspresi menyangkut masuknya karakter tokoh cerita pada jiwa pendongeng, sehingga apa yang dikatakan oleh pendongeng itu bukan mewakili dirinya, tetapi mewakili tokoh yang diperankan. Ekspresi ini dapat menyangkut bagaimana karakter tokoh yang keras, lembut, lucu, dan lugu. ', 'a', 1),
(10, 'Untuk dapat memudahkan pendengar dalam menyimak makna cerita, maka si pencerita harus menguasai…', 'Penguasaan materi yang dimaksud adalah bagaimana seorang pendongeng mampu menguasai alur cerita secara utuh. Keutuhan alur ini dapat memudahkan pendengar dalam menyimak makna cerita.', 'b', 2),
(11, 'Pesan atau amanat dalam drama adalah pesan yang ingin disampaikan pengarang kepada publiknya, teknik penyampaian pesan harus sesuai dengan…', 'Penyampaian pesan atau amanat dalam sebuah cerita sama halnya dengan tulisan ilmiah, apa yang dipertanyakan didepan itulah yang ditarik dalam kesimpulan yang nantinya akan dijadikan sebuah amanat, maka si penulis atau si pencerita harus benar-benar memahami pesan atau amanat yang terkandung dalam cerita', 'c', 3),
(12, 'Penulis naskah lakon menciptakan untuk menyuguhkan persoalan kehidupan manusia, baik kehidupan lahiriah maupun kehidupan batiniah yakni…', 'Penulis naskah lakon menciptakan untuk menyuguhkan persoalan kehidupan manusia, baik kehidupan lahiriah maupun kehidupan batiniah, yakni  pikiran (cita), perasaan (rasa), dan kehendak (karsa). ', 'd', 4),
(13, 'Dalam mesatua Bali secara tersirat memiliki fungsi bagi…', 'Dalam satua Bali secara tersirat memiliki fungsi bagi masyarakat dan kebudayaan Bali, fungsi tersebut dapat berupa fungsi sosial yang berkaitan dengan konsep-konsep budaya dan agama Hindu yang dituangkan di dalam satua (cerita). ', 'a', 1),
(14, 'Satua Bali dapat juga digolongkan menjadi beberapa macam, yaitu…', 'Satua Bali dapat juga digolongkan menjadi fable, legenda, dan mitos.', 'd', 4),
(15, 'Tiap-tiap dongeng mempunyai beberapa variasi yang berbeda menurut tempat dan pendongengnya, oleh karena itu dongeng disampaikan secara…', 'Tiap-tiap dongeng mempunyai beberapa variasi yang berbeda menurut tempat dan pendongengnya, oleh karena itu dongeng disampaikan secara oral/tertulis.', 'b', 2),
(16, 'Untuk dapat membawakan satua yang menarik maka kita harus menguasai (kecuali)…', 'Agar dapat membawakan satua yang menarik maka kita harus menguasai teknik dasar masatua Bali terlebih dahulu.', 'c', 3),
(17, 'Menurut Bapak I Made Taro dalam mesatua Bali point yang paling penting adalah…', 'Dalam mesatua ada tiga unsur utama yang harus dikuasai yaitu wiraga, wirama, dan wirasa, dari ketiga unsur tersebut yang paling penting ialah wirasa atau penghayatan.', 'b', 2),
(18, 'Struktur bahasa yang digunakan dalam satua Bali pada umumnya menggunakan bahasa kepara, namun tetap disesuaikan  dengan anggah ungguhing (tingkatan-tingkatan) dalam berbahasa Bali yang terdiri dari…', 'Struktur bahasa yang digunakan dalam satua Bali pada umumnya menggunakan bahasa kepara atau bahasa yang digunakan dalam kehidupan sehari-hari ketika mengadakan interaksi dengan lawan bicaranya. Namun tetap disesuaikan  dengan anggah ungguhing (tingkatan-tingkatan) dalam berbahasa Bali seperti AMI (alus mider), ASI (alus singgih), ASO (alus sor), dan BK (basa kasar). ', 'd', 4),
(19, 'Judul cerita yang ditampilkan pada menu penampilan mesatua secara keseluruhan adalah…', 'Judul cerita yang dibawakan adalah Siap Sangkur Mataluh Emas', 'c', 3),
(20, 'Teknik penyampaian pesan dapat dibagi menjadi…', 'Teknik penyampaian pesan tersebut dapat secara langsung maupun tidak langsung.', 'a', 1);

